mapper.select:
SELECT uid ,tid,oid,univalent*quantity as deal_amount,deal_date ,status,type,univalent,quantity,phone
from ftc_deal where status =6 and type = 2 and  DATE_SUB(CURDATE(), INTERVAL 30 DAY)  <=  date(deal_date)

    and phone = #{phone}

 order by deal_date desc

SELECT uid ,tid,oid,univalent*quantity as deal_amount,deal_date ,status,type,univalent,quantity,phone
from ftc_deal where DATE_SUB(CURDATE(), INTERVAL 30 DAY)  <=  date(deal_date)

   and status = #{status}


   and  type =#{type}

order by deal_date desc

SELECT count(0)
from ftc_deal where DATE_SUB(CURDATE(), INTERVAL 30 DAY )  <=  date(deal_date)

    and status = #{status}


    and  type =#{type}

SELECT uid ,tid,oid, deal_amount,deal_date ,status,type,univalent,quantity,phone
from ftc_deal where 1=1
    and status=#{status}
    and type=#{type}
 order by deal_date desc

SELECT IFNULL(count(0),0)
from ftc_deal where 1=1
    and status=#{status}
    and type=#{type}
